tps://ps.w.org/wordpress-seo/assets/icon.svg?rev=1946641";}s:7:"banners";a:2:{s:2:"2x";s:69:"https://ps.w.org/wordpress-seo/assets/banner-1544x500.png?rev=1843435";s:2:"1x";s:68:"https://ps.w.org/wordpress-seo/assets/banner-772x250.png?rev=1843435";}s:11:"banners_rtl";a:2:{s:2:"2x";s:73:"https://ps.w.org/wordpress-seo/assets/banner-1544x500-rtl.png?rev=1843435";s:2:"1x";s:72:"https://ps.w.org/wordpress-seo/assets/banner-772x250-rtl.png?rev=1843435";}s:6:"tested";s:5:"4.9.8";s:12:"requires_php";s:5:"5.2.4";s:13:"compatibility";O:8:"stdClass":0:{}}}s:12:"translations";a:0:{}s:9:"no_update";a:3:{s:43:"list-category-posts/list-category-posts.php";O:8:"stdClass":9:{s:2:"id";s:33:"w.org/plugins/list-category-posts";s:4:"slug";s:19:"list-category-posts";s:6:"plugin";s:43:"list-category-posts/list-category-posts.php";s:11:"new_version";s:4:"0.79";s:3:"url";s:50:"https://wordpress.org/plugins/list-category-posts/";s:7:"package";s:67:"https://downloads.wordpress.org/plugin/list-category-posts.0.79.zip";s:5:"icons";a:2:{s:2:"2x";s:71:"https://ps.w.org/list-category-posts/assets/icon-256x256.png?rev=978341";s:2:"1x";s:71:"https://ps.w.org/list-category-posts/assets/icon-128x128.png?rev=978341";}s:7:"banners";a:1:{s:2:"1x";s:73:"https://ps.w.org/list-category-posts/assets/banner-772x250.png?rev=978337";}s:11:"banners_rtl";a:0:{}}s:23:"ml-slider/ml-slider.php";O:8:"stdClass":9:{s:2:"id";s:23:"w.org/plugins/ml-slider";s:4:"slug";s:9:"ml-slider";s:6:"plugin";s:23:"ml-slider/ml-slider.php";s:11:"new_version";s:6:"3.10.0";s:3:"url";s:40:"https://wordpress.org/plugins/ml-slider/";s:7:"package";s:59:"https://downloads.wordpress.org/plugin/ml-slider.3.10.0.zip";s:5:"icons";a:3:{s:2:"2x";s:62:"https://ps.w.org/ml-slider/assets/icon-256x256.png?rev=1837669";s:2:"1x";s:54:"https://ps.w.org/ml-slider/assets/icon.svg?rev=1837669";s:3:"svg";s:54:"https://ps.w.org/ml-slider/assets/icon.svg?rev=1837669";}s:7:"banners";a:2:{s:2:"2x";s:65:"https://ps.w.org/ml-slider/assets/banner-1544x500.png?rev